% Typical usage:
% :- time(a16(16, AN)).
% a16(N, AN) can also be used for any N < 16.

:- dynamic count/1.

a16(N, AN) :-
	retractall(count(_)),
	assert(count(0)),
	forall(possible_Q(N, _Q),
	(
		count(C),
		retract(count(C)),
		CC is C + 1,
		assert(count(CC))
	)),
	count(AN).

possible_Q(N, Q) :-
